class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: int
        """
        dic = {}
        for char in s:
            dic[char] = dic.get(char,0) + 1
        x = 0
        
        oddsThere = False
        for key in dic.keys():
            if (dic[key]  % 2 == 0):
                x += dic[key]
            else:
                x += (dic[key] -1)
                oddsThere = True
            
        if(oddsThere):
            x += 1
        
        return x   
            
